import base64
from Crypto.Util.number import bytes_to_long
frequen="ETAOINSHRDLCUMWFGYPBVKJXQZ"
frequen=frequen[::-1]
def hamming_distance(a: bytes, b: bytes) -> int:
    if len(a) != len(b):
        raise ValueError("Hai chuỗi phải cùng độ dài")
    a = bytes_to_long(a)
    b = bytes_to_long(b)
    return ((a ^ b).bit_count())
def normalize(a :bytes, b:int) -> float:
    blocks=[a[i*b:(i+1)*b] for i in range ( 0, 6 )]
    val=[hamming_distance(blocks[i],blocks[i+1] )/b for i in range (0,5)]
    return sum(val)/len(val)
def scorechar(s: bytes)->int:
    score=0
    for byte in s:
        if byte<32 or byte>126:
            score-=10
        ch=chr(byte)
        ch=ch.upper()
        if ch in frequen:
            score+=frequen.index(ch)
        if ch == 'Z':
            score+=1
        if ch == ' ':
            score+=5
        if ch in ".,'!?;:-":
            score+=2
    return score
encrypted=""
decoded = base64.b64decode(encrypted)
score=sorted(((k,normalize(decoded, k)) for k in range (2,41)) ,key=lambda x:x[1])
best_keysizes = [k for k,_ in score[:5]]
bestsc=0
#k=[2, 5, 3, 29, 31]
for k in best_keysizes:
    blocks=[decoded[i::k] for i in range (0, k)]
    best_key=b""
    for j in range (k):
        best=0
        for i in range(256):
           xor=bytes(i^byte for byte in blocks[j])
           score=scorechar(xor)
           if score>best:
               best=score
               x=bytes([i])
        best_key+=x
    xor_decrypted=b"".join(bytes(b ^ best_key[i % k] for i, b in enumerate(decoded)) for block in blocks)
    sc=scorechar(xor_decrypted)
    if sc>bestsc:
        bestsc=sc
        beststr=xor_decrypted
        bestkey=best_key
print("Key:", bestkey.decode())
print(beststr.decode())
